  BEFORE INSERT
  ON Customer
  FOR EACH ROW
  BEGIN
    IF new.email NOT REGEXP '^[A-Z0-9._%-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$'
    THEN
      SIGNAL SQLSTATE '45000'
      SET MESSAGE_TEXT = 'Email is invalid!';
